from machine import I2C,UART,Pinfrom  Sengo2  import *import timeimport randomnum = 5  #设置检测数量最多5个x = 0y = 0i = 0def result_output():    global num, x, y, i    # Sengo2不主动返回检测识别结果，需要主控板发送指令进行读取。读取的流程：首先读取识别结果的数量，接收到指令后，Sengo2会刷新结果数据，如果结果数量不为零，那么主控再发送指令读取结果的相关信息。请务必按此流程构建程序。    obj_num = sengo2.GetValue(sengo2_vision_e.kVisionColor, sentry_obj_info_e.kStatus)    if obj_num:        print("Totally %d points: "%( obj_num ))        print("|",end='')        for i in range(1, (obj_num + 1), 1):            #获取第i个物体的标签（颜色ID）            label = sengo2.GetValue(sengo2_vision_e.kVisionColor,sentry_obj_info_e.kLabel,i)            print(label,end='|')            time.sleep(0.2)        print("\n")        # 等待Sengo2完成操作系统的初始化。此等待时间不可去掉，避免出现Sengo2尚未初始化完毕主控器已经开发发送指令的情况time.sleep(2)# 选择UART或者I2C通讯模式，Sengo2出厂默认为I2C模式，短按模式按键可以切换# 4种UART通讯模式：UART9600（标准协议指令），UART57600（标准协议指令），UART115200（标准协议指令），Simple9600（简单协议指令），########################################################################################################## port = UART(2,rx=Pin(16),tx=Pin(17),baudrate=9600)i2c = I2C(0,scl=Pin(21),sda=Pin(20),freq=400000)# Sengo2通讯地址：0x60。如果I2C总线挂接多个设备，请避免出现地址冲突sengo2 = Sengo2(0x60)err = sengo2.begin(i2c)print("sengo2.begin: 0x%x"% err)# 设置Sengo2两颗LED的颜色，可选：黑Close，白White，红Red，绿Green，黄Yellow，蓝Blue，紫Purple，青Cyan# 三个参数依次为：检测识别到目标时的颜色，未发现目标的颜色，亮度# 当环境光亮度不足时，可将LED的颜色全部设置为白White，并且将亮度值设置为最大15（范围：1-15）# 无法分别设置两颗LEDsengo2.LedSetColor(sentry_led_color_e.kLedBlue,sentry_led_color_e.kLedBlue,1)# 算法的默认参数，如果不用更改，则可以删除本行代码sengo2.SetParamNum(sengo2_vision_e.kVisionColor,1)# 算法的默认参数，如果不用更改，则可以删除本行代码sengo2.SetParam(sengo2_vision_e.kVisionColor,[50, 50, 3, 4, 0],1)  err = sengo2.VisionBegin(sengo2_vision_e.kVisionColor)print("sengo2.VisionBegin(sengo2_vision_e.kVisionColor):0x%x"% err)#设置颜色识别算法num组参数,通过设置num控制组参数的数量sengo2.SetParamNum(sengo2_vision_e.kVisionColor,num)while True:    # num = 5 也就是循环5次分别对应5个识别框    for i in range(1, (num + 1), 1):        #设置识别框显示在屏幕上的x轴坐标        x = (round(100 * i / (num + 1)))        #设置识别框显示在屏幕上的y坐标        y = 50        #设置识别框显示在屏幕上的宽度        w = (round(i * 2 + 1))        #设置识别框显示在屏幕上的高度        h = (round(i * 2 + 1))        #写入识别框参数        sengo2.SetParam(sengo2_vision_e.kVisionColor,[x, y, w, h, 0],i)        #延时0.1S        time.sleep(0.1)    result_output()    time.sleep(0.3)