from machine import I2C, Pinimport timeimport randomfrom Sengo2 import *# 初始化I2C (根据实际接线调整引脚)i2c = I2C(0, scl=Pin(21), sda=Pin(20), freq=400000)  # 使用参考代码中的引脚# 等待Sengo2初始化 (重要!)time.sleep(2)# 创建Sengo2对象sentry = Sengo2()# 开始传感器if sentry.begin(i2c) != SENTRY_OK:    print("Sentry initialization failed!")    while True: pass# 设置色块检测参数 - 只检测红色和蓝色sentry.SetParamNum(sengo2_vision_e.kVisionBlob, 2)  # 设置2组参数# 红色参数组sentry.SetParam(    sengo2_vision_e.kVisionBlob,    [0, 0, 10, 10, color_label_e.kColorRed],  # 参数格式    param_id=1)# 蓝色参数组sentry.SetParam(    sengo2_vision_e.kVisionBlob,    [0, 0, 10, 10, color_label_e.kColorBlue],  # 参数格式    param_id=2)# 开启色块检测功能if sentry.VisionBegin(sengo2_vision_e.kVisionBlob) == SENTRY_OK:    print("Color block detection enabled")# 主检测循环try:    while True:        # 获取检测到的色块数量        obj_num = sentry.GetValue(sengo2_vision_e.kVisionBlob,sentry_obj_info_e.kStatus)                if obj_num:            print(f"Detected {obj_num} color block")                        # 处理每个检测到的色块            for i in range(1, obj_num + 1):                # 获取颜色标签                color_label = sentry.GetValue(                    sengo2_vision_e.kVisionBlob,                    sentry_obj_info_e.kLabel,                    i                )                                 # 只处理红色和蓝色                if color_label in [color_label_e.kColorRed, color_label_e.kColorBlue]:                    # 获取位置和大小                    x = sentry.GetValue(                        sengo2_vision_e.kVisionBlob,                        sentry_obj_info_e.kXValue,                        i                    )                    y = sentry.GetValue(                        sengo2_vision_e.kVisionBlob,                        sentry_obj_info_e.kYValue,                        i                    )                    w = sentry.GetValue(                        sengo2_vision_e.kVisionBlob,                        sentry_obj_info_e.kWidthValue,                        i                    )                    h = sentry.GetValue(                        sengo2_vision_e.kVisionBlob,                        sentry_obj_info_e.kHeightValue,                        i                    )                                        # 确定颜色名称                    color_name = "Red" if color_label == color_label_e.kColorRed else "Blue"                                        print(f"{color_name}color block {i}: X={x}, Y={y}, Width={w}, Height={h}")                time.sleep(0.1)  # 短暂延时except KeyboardInterrupt:    sentry.VisionEnd(sengo2_vision_e.kVisionBlob)    print("The program has stopped")